import UnCours from './UnCours';

export default function ListeCours({session, cours, randomOrder, limite}){

    useEffect(() => {
        console.log(cours);
        if (randomOrder === true) {
            console.log("MELANGER");
            console.log(cours);
            cours = melangerTableau(cours);
            console.log(cours);
        }

    }, []);

    return (
        <section className="ListeCours">
            <h1>{session}</h1>
            {
                cours.map(
                    unCours =>
                        <UnCours titre={unCours.titre} desc={unCours.description} numeroCours={unCours.numero_du_cours} />
                )
            }
        </section>
    );
}
