      key: 'cours',
      path: '/galerie-des-cours',
      component: () => <Cours/>
    }, 
    {
      key: 'social',
      path: '/le-social',
      component: () => <Social/>
    },
    {
      key: 'avenir',
      path: '/avenir',
      component: () => <Avenir/>
    },
    {
      key: 'projets',
      path: '/galerie-des-projets',
      component: () => <Projets/>
    }, 
    {
      key: 'enseignants',
      path: '/les-enseignants',
      component: () => <Enseignants/>
    }, 
  ];

