import { useContext } from 'react';
import ListeCours from './ListeCours';
import './ListeSessions.scss';

export default function ListeSessions({sessions}){

    const siteData = useContext(useContext);

    return (
        <section className="ListeSessions">
            {
                sessions.map(
                    session => 
                        <ListeCours key={siteData.session.titre} session={siteData.session.title.rendered} cours={siteData.session.cours} randomOrder={true}/>
                )
            }
        </section>
    );
}
