import { useEffect, useState } from "react";
import useFetch from './useFetch';

export default async function useChargerSite(){

    const [siteData, setSiteData] = useState(null);

    const dataToFetch = [
        'http://localhost:8080/projet-web-5w5/index.php/wp-json/wp/v2/pages',
        'http://localhost:8080/projet-web-5w5/index.php/wp-json/wp/v2/session',
    ];
    useEffect(() => {
        Promise.all([
            fetch('http://localhost:8080/projet-web-5w5/index.php/wp-json/wp/v2/pages').then(resp => resp.json()),
            fetch('http://localhost:8080/projet-web-5w5/index.php/wp-json/wp/v2/session').then(resp => resp.json()),
        ]).then(data => {
            console.log(data);
            setSiteData(data);
        });
    }, [])


    return siteData;
}
