import './Appli.scss';
import { Routes, Route } from 'react-router-dom';
import { useState } from 'react';
import useFetch from './Hooks/useFetch';
import Home from './Home';
import Navigation from './Navigation';
import Burger from './Burger';

export default function Appli() {

  const pages = useFetch('http://localhost:8080/projet-web-5w5/index.php/wp-json/wp/v2/pages');

  const [navOpenState, setNavOpenState] = useState(false);

    const toggleMenu = () => {
      setNavOpenState(!navOpenState);
  };

  return (
    <div className="Appli">
      <Burger onClick={toggleMenu}/>
      <Navigation navOpenState={navOpenState} pages={pages}/>
      <Routes>
        {
          (pages != null)
          ?
          pages.map(
            page => (page.slug == 'accueil') 
            ?
            <Route key={page.id} path={'/'} element={<Home/>}></Route>
            :
            page => (page.slug == 'le-social') 
            ?
            <Route key={page.id} path={'/'} element={<Home/>}></Route>
            :page => (page.slug == 'les-enseignants') 
            ?
            <Route key={page.id} path={'/'} element={<Home/>}></Route>
            :page => (page.slug == 'avenir') 
            ?
            <Route key={page.id} path={'/'} element={<Home/>}></Route>
            :page => (page.slug == 'galerie-des-cours') 
            ?
            <Route key={page.id} path={'/'} element={<Home/>}></Route>
            :page => (page.slug == 'galerie-des-cours') 
            ?
            <Route key={page.id} path={'/'} element={<Home/>}></Route>
            :page => (page.slug == 'galerie-des-projets') 
            ?
            <Route key={page.id} path={'/'} element={<Home/>}></Route>
            :
            <Route key={page.id} path={'/'} element={<Home/>}></Route>
          )
          :
          <Route>No pages...</Route>
        }
      </Routes>
    </div>
  );
}
