import { useEffect, useState } from "react";
import useFetch from './useFetch';

export default async function useChargerSite(){

    const dataToFetch = {
        pages: [],
        sessions: [],
        cours: [],
        enseignants: [],
    };
    useEffect(() => {
        Promise.all([
            fetch('http://localhost:8080/projet-web-5w5/index.php/wp-json/wp/v2/pages').then(resp => resp.json()),
            fetch('http://localhost:8080/projet-web-5w5/index.php/wp-json/wp/v2/session').then(resp => resp.json()),
            fetch('http://localhost:8080/projet-web-5w5/index.php/wp-json/wp/v2/cours').then(resp => resp.json()),
            fetch('http://localhost:8080/projet-web-5w5/index.php/wp-json/wp/v2/enseignants').then(resp => resp.json()),
        ]).then(data => {
            dataToFetch.pages = data[0];
            dataToFetch.sessions = data[1];
            dataToFetch.cours = data[2];
            dataToFetch.enseignants = data[3];
        });
    }, [])

    return dataToFetch;
}
