import { useEffect, useState } from "react";

export default async function useChargerPage(){
    const [data, setData] = useState([]);
    useEffect(() => {
        async function chargerData() {
            const pagesData = await useFetch('http://localhost:8080/projet-web-5w5/index.php/wp-json/wp/v2/pages');
            const sessionsData = await useFetch('http://localhost:8080/projet-web-5w5/index.php/wp-json/wp/v2/session');
            const fetchedData = [pagesData, sessionsData];

            setData(fetchedData);
        }

        chargerData();
    }, []);
    return data;
}
